import React from "react";

function QuestionTest() {
  const [question, setQuestion] = useState(null);
  const [selectedOption, setSelectedOption] = useState(null);
  const [answered, setAnswered] = useState(false);
  const { increaseScore, reduceLife, lifes, restartGame } = useContext(ScoreContext);
  const { getQuestion, gameTopic } = useContext(GameContext);
  const [round, setRound] = useState(0);
  const [gameOver, setGameOver] = useState(false);

  function checkAnswer(selectedOption) {
    setSelectedOption(selectedOption);
   setAnswered(true);
   if (selectedOption === question.answer) {
     increaseScore();
   } else {
     reduceLife();
   }
   setRound((prevRound) => prevRound + 1);
 }

 useEffect(() => {
   if (lifes === 0) {
     setGameOver(true);
   } else if (round > 0) {
     const timeout = setTimeout(() => {
       setSelectedOption(null);
       setAnswered(false);
       setQuestion(getQuestion());
     }, 2000);
     return () => clearTimeout(timeout);
   }
 }, [round, lifes, getQuestion]);

 useEffect(() => {
   setQuestion(getQuestion());
 }, [getQuestion]);

 const restart = () => {
   setRound(0);
   setGameOver(false);
   restartGame();
   setAnswered(false);
   setQuestion(getQuestion());
 };

 const getDetailAnswer = (municipi) => {
   switch (gameTopic) {
     case 'population':
       return municipi.poblacio + ' habitants';
     case 'altitude':
       return municipi.altitud + ' m';
     case 'surface':
       return municipi.superficie + ' m2';
     default:
       return 'Not rendered';
   }
 }

 if (gameOver) {
   return <GameOverScreen onRestart={restart} />;
 }

 if (!question) {
   return <div className="message">Loading...</div>;
 }


  return (
    <div className="question-test">
      <h2 className="question">
        This is the question abouT your prefered topic
      </h2>
      <button className="game-buttons"> Answer 2 </button>
      <button className="game-buttons"> Answer 3 </button>
      <button className="game-buttons"> Answer 1 </button>
      <button className="game-buttons"> Answer 4 </button>
    </div>
  );
}

export default QuestionTest;
