import comarques from "../data/municipis"

function randomIndex(num) {
    return Math.floor(Math.random() * num);
  }

function comarcaTestMaker(title, questionVariable) {
    return () => {
        
        const ids = new Set();
        
        while (ids.size < 4) {
            ids.add(randomIndex(comarques.length));
        }

        const options = []
        
        for (id of ids) {
            options.push(comarques[id]);
        };
        
        const question = {
            title,
            answer1: options[0],
            answer2: options[1],
            answer3: options[2],
            answer4: options[3],
            question: options[randomIndex(indices.size)][questionVariable],
          };
          return question;

    };
}

const capitalComarca = comarcaTestMaker("Quina és la capital de la comarca?", "comarca");
const comarcaCapital = comarcaTestMaker("De quina comarca és capital?", "comarca");
const comarcaProvincia = comarcaTestMaker("A quina provincia es troba aquesta provincia?", "comarca");

export {capitalComarca, comarcaCapital, comarcaProvincia};
