import React, { useState, useContext, useEffect } from "react";
import ScoreContext from "./context/ScoreContext";
import GameContext from "./context/GameContext";
import GameOverScreen from "./GameOVerScreen";


function QuestionTest() {
  const [question, setQuestion] = useState(null);
  const [selectedOption, setSelectedOption] = useState(null);
  const [answered, setAnswered] = useState(false);
  const { increaseScore, reduceLife, lifes, restartGame } = useContext(ScoreContext);
  const { getQuestion, gameTopic } = useContext(GameContext);
  const [round, setRound] = useState(0);
  const [gameOver, setGameOver] = useState(false);

  function checkAnswer(selectedOption) {
   setSelectedOption(selectedOption);
   setAnswered(true);
   if (selectedOption === question.correctOption) {
     increaseScore();
   } else {
     reduceLife();
   }
   setRound((prevRound) => prevRound + 1);
 }

 useEffect(() => {
   if (lifes === 0) {
     setGameOver(true);
   } else if (round > 0) {
     const timeout = setTimeout(() => {
       setSelectedOption(null);
       setAnswered(false);
       setQuestion(getQuestion());
     }, 2000);
     return () => clearTimeout(timeout);
   }
 }, [round, lifes, getQuestion]);

 useEffect(() => {
   setQuestion(getQuestion());
 }, [getQuestion]);

 const restart = () => {
   setRound(0);
   setGameOver(false);
   restartGame();
   setAnswered(false);
   setQuestion(getQuestion());
 };


 if (gameOver) {
   return <GameOverScreen onRestart={restart} />;
 }

 if (!question) {
   return <div className="message">Loading...</div>;
 }


  return (
    <div className="question-test">
      <h2 className="question">{question.title}</h2>
      <h4>{question.answer}</h4>

      <button onClick={() => checkAnswer(question.option1)}
          className="game-buttons"
          disabled={answered}
          style={{
            backgroundColor:
              answered && selectedOption === question.option1
                ? question.option1 === question.correctOption
                  ? "green"
                  : "red"
                : "initial",
          }}>{question.option1}
{/* TODO add detailed description once selected */}
        </button>

        <button onClick={() => checkAnswer(question.option2)}
          className="game-buttons"
          disabled={answered}
          style={{
            backgroundColor:
              answered && selectedOption === question.option2
                ? question.option2 === question.correctOption
                  ? "green"
                  : "red"
                : "initial",
          }}>{question.option2}
{/* TODO add detailed description once selected */}
        </button>

        <button onClick={() => checkAnswer(question.option3)}
          className="game-buttons"
          disabled={answered}
          style={{
            backgroundColor:
              answered && selectedOption === question.option3
                ? question.option3 === question.correctOption
                  ? "green"
                  : "red"
                : "initial",
          }}>{question.option3}
{/* TODO add detailed description once selected */}
        </button>

        <button onClick={() => checkAnswer(question.option4)}
          className="game-buttons"
          disabled={answered}
          style={{
            backgroundColor:
              answered && selectedOption === question.option4
                ? question.option4 === question.correctOption
                  ? "green"
                  : "red"
                : "initial",
          }}>{question.option4}
{/* TODO add detailed description once selected */}
        </button>
    </div>
  );
}

export default QuestionTest;
